import React, { useState } from "react";
import "./App.css";
import Form from "./Input/Form.js";
import List from "./List/List.js";

function App(props) {
  const [lista, setLista] = useState([
    {
      id: "elso",
      name: "Joe",
      age: 23,
    },
    {
      id: "masodik",
      name: "Eger",
      age: 38,
    },
  ]);

  const formSubmitHandler = (listaButtonrol) => {
    console.log(listaButtonrol);
    let listaUj = [...lista, listaButtonrol];
    setLista(listaUj);
    console.log(listaUj);
  };
  const deleteItemHandler = (id) => {
    console.log(id);

    setLista((lista) => {
      const listaUj = lista.filter((szurtTetel) => szurtTetel.szam !== id);
      return listaUj;
    });

    /*  setCourseGoals(prevGoals => {
      const updatedGoals = prevGoals.filter(goal => goal.id !== goalId);
      return updatedGoals;
    }); */
  };

  return (
    <div>
      <Form onSubmitValami={formSubmitHandler}></Form>
      <List onDeleteItem={deleteItemHandler} ertekek={lista} />
    </div>
  );
}

export default App;
