        console.log(event.target.value)
    }

  return (

    <div id="goal-form">
    <label>Todo:</label>
    <input type="text" name="first_name" className=" " onChange={todoInputChangeHandler} />

    <Button className="centerButton" type="submit">Add Goal</Button>
    </div>
    )
};

export default Form;
