import './App.css';
import Form from './Input/Form.js';
import List from './List/List.js';


function App(props) {

  const [lista, setLista ] = useState({
    key: 'elso',
    name: 'Joe',
    age: 23

  }, 
  {
    key: 'masodik',
    name: 'Eger',
    age: 38

  }, 
) 
  const formSubmitHandler = (ezt) => {
    setLista(listaRegi => listaRegi.map(itemek => console.log(itemek)))

  } 



  return (
    <div>
      <Form  onSubmitValami={formSubmitHandler}></Form>

      <List ertekek={lista}/>
    </div>
  );
}

export default App;


{props.items.map( (expense)=> (

  <ExpenseItem 
